def serialize(title,ingreds):
    vals=[]
    total=0
    for i in featureKeys:
        if i in ingreds:
            vals.append(str(ingreds[i]))
            total=total+1
        else:
            vals.append("0")
    vals[0]= title
    vals[1]= str(total)
    if total < 20:
        return
    return str(vals).lstrip("[").rstrip("]")

def getText(nodelist):
    rc = []
    for node in nodelist:
        if node.nodeType == node.TEXT_NODE:
            rc.append(node.data)
    return ''.join(rc)

