        title=handleTitle(page.getElementsByTagName("title")[0])
        text=""
        features=[]
        if not ":" in title:
                text=handleText(page.getElementsByTagName("text")[0])
                ingreds=handleIngredients(text)
                s=serialize(title,ingreds)
                if s:
                    f = open('./cookbook/'+title+'.txt', 'w')
                    f.write (text)
